import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Books.css';

const Books = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const allBooks = [
    {
      id: 1,
      title: "The Midnight Library",
      author: "Matt Haig",
      cover: "https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1602190253i/52578297.jpg",
      rating: 4.5,
      genre: "Fiction",
      available: true
    },
    {
      id: 2,
      title: "Atomic Habits",
      author: "James Clear",
      cover: "https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1655988385i/40121378.jpg",
      rating: 4.8,
      genre: "Self-Help",
      available: true
    },
    {
      id: 3,
      title: "Project Hail Mary",
      author: "Andy Weir",
      cover: "https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1597695864i/54493401.jpg",
      rating: 4.7,
      genre: "Science Fiction",
      available: false
    }
  ];

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleQuickView = (bookId) => {
    navigate(`/quick-view/${bookId}`);
  };

  const filteredBooks = allBooks.filter(book => 
    book.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    book.author.toLowerCase().includes(searchQuery.toLowerCase()) ||
    book.genre.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="books-container">
      <div className="books-header">
        <h1>Library Books</h1>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search by title, author, or genre..."
            value={searchQuery}
            onChange={handleSearch}
          />
        </div>
      </div>

      <div className="books-grid">
        {filteredBooks.map(book => (
          <div key={book.id} className="book-card">
            <div className="book-cover">
              <img src={book.cover} alt={book.title} />
              <div className="book-overlay">
                <button 
                  className="quick-view-btn"
                  onClick={() => handleQuickView(book.id)}
                >
                  Quick View
                </button>
                <button 
                  className={`borrow-btn ${!book.available ? 'disabled' : ''}`}
                  disabled={!book.available}
                >
                  {book.available ? 'Borrow' : 'Not Available'}
                </button>
              </div>
            </div>
            <div className="book-info">
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <div className="book-details">
                <span className="rating">{'⭐'.repeat(Math.floor(book.rating))} {book.rating}</span>
                <span className="genre">{book.genre}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Books;
