import React, { useState } from 'react';
import './Home.css';

const Home = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  const featuredBooks = [
    {
      id: 1,
      title: "The Great Gatsby",
      author: "F. Scott Fitzgerald",
      cover: "https://example.com/gatsby.jpg",
      rating: 4.5,
      category: "Fiction"
    },
    // Add more featured books
  ];

  const newArrivals = [
    {
      id: 1,
      title: "The Midnight Library",
      author: "Matt Haig",
      cover: "https://example.com/midnight.jpg",
      date: "2025-01-25"
    },
    // Add more new arrivals
  ];

  const announcements = [
    {
      id: 1,
      title: "Summer Reading Challenge",
      date: "2025-02-01",
      description: "Join our summer reading challenge and win exciting prizes!"
    },
    // Add more announcements
  ];

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle('dark-mode');
  };

  return (
    <div className={`home-container ${isDarkMode ? 'dark-mode' : ''}`}>
      <div className="theme-toggle">
        <button onClick={toggleTheme}>
          {isDarkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
        </button>
      </div>

      {/* Hero Section */}
      <section className="hero-section">
        <h1>Welcome to Your Library</h1>
        <div className="search-bar">
          <input type="text" placeholder="Search books by title, author, or category..." />
          <button>üîç Search</button>
        </div>
      </section>

      {/* Featured Books */}
      <section className="featured-section">
        <h2>Featured Books</h2>
        <div className="books-grid">
          {featuredBooks.map(book => (
            <div key={book.id} className="book-card">
              <img src={book.cover} alt={book.title} />
              <div className="book-info">
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <div className="rating">{'‚≠ê'.repeat(Math.floor(book.rating))}</div>
                <span className="category">{book.category}</span>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* New Arrivals */}
      <section className="new-arrivals-section">
        <h2>New Arrivals</h2>
        <div className="books-grid">
          {newArrivals.map(book => (
            <div key={book.id} className="book-card">
              <img src={book.cover} alt={book.title} />
              <div className="book-info">
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <span className="new-badge">New</span>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Book of the Day */}
      <section className="book-of-day-section">
        <h2>Book of the Day</h2>
        <div className="featured-book">
          <img src="https://example.com/book-of-day.jpg" alt="Book of the Day" />
          <div className="book-details">
            <h3>The Silent Patient</h3>
            <p className="author">Alex Michaelides</p>
            <p className="description">
              A psychological thriller that will keep you guessing until the last page...
            </p>
            <button className="borrow-btn">Borrow Now</button>
          </div>
        </div>
      </section>

      {/* Announcements & Events */}
      <section className="announcements-section">
        <h2>Library Updates</h2>
        <div className="announcements-grid">
          {announcements.map(announcement => (
            <div key={announcement.id} className="announcement-card">
              <h3>{announcement.title}</h3>
              <p className="date">{announcement.date}</p>
              <p>{announcement.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Library Information */}
      <section className="library-info-section">
        <h2>Visit Us</h2>
        <div className="info-grid">
          <div className="location-info">
            <h3>üìç Main Branch</h3>
            <p>123 Library Street</p>
            <p>Open: Mon-Sat, 9 AM - 8 PM</p>
          </div>
          <div className="contact-info">
            <h3>üìû Contact Us</h3>
            <p>Phone: (555) 123-4567</p>
            <p>Email: library@example.com</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
