import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../../context/AuthContext';
import { useBorrow } from '../../../context/BorrowContext';
import './Books.css';

const Books = () => {
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const { borrowBook } = useBorrow();
  const [selectedCategory, setSelectedCategory] = useState('All');

  const books = [
    {
      id: 1,
      title: "The Psychology of Money",
      author: "Morgan Housel",
      cover: "https://m.media-amazon.com/images/I/71TRUbzcvaL._AC_UF1000,1000_QL80_.jpg",
      rating: 4.6,
      category: "Finance",
      description: "Timeless lessons on wealth, greed, and happiness doing well with money isn't necessarily about what you know. It's about how you behave. And behavior is hard to teach, even to really smart people.",
      publishedDate: "2020-09-08"
    },
    {
      id: 2,
      title: "Atomic Habits",
      author: "James Clear",
      cover: "https://m.media-amazon.com/images/I/81bGKUa1e0L._AC_UF1000,1000_QL80_.jpg",
      rating: 4.8,
      category: "Self-Help",
      description: "No matter your goals, Atomic Habits offers a proven framework for improving--every day. James Clear, one of the world's leading experts on habit formation, reveals practical strategies that will teach you exactly how to form good habits, break bad ones, and master the tiny behaviors that lead to remarkable results.",
      publishedDate: "2018-10-16"
    },
    {
      id: 3,
      title: "Deep Work",
      author: "Cal Newport",
      cover: "https://m.media-amazon.com/images/I/71QKQ9mwV7L._AC_UF1000,1000_QL80_.jpg",
      rating: 4.5,
      category: "Productivity",
      description: "Deep work is the ability to focus without distraction on a cognitively demanding task. It's a skill that allows you to quickly master complicated information and produce better results in less time.",
      publishedDate: "2016-01-05"
    },
    {
      id: 4,
      title: "பொன்னியின் செல்வன்",
      author: "கல்கி கிருஷ்ணமூர்த்தி",
      cover: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSHd7H5dXOZgxAHBGzQcYQXiJX2JYdvBPJ1Ow&usqp=CAU",
      rating: 4.9,
      category: "Historical Fiction",
      description: "A masterpiece of Tamil historical fiction that follows the story of Arulmozhivarman, who later became Raja Raja Chola I. Set in the 10th century, it weaves together political intrigue, romance, and adventure in the Chola empire.",
      publishedDate: "1951-10-01"
    },
    {
      id: 5,
      title: "The Creative Act",
      author: "Rick Rubin",
      cover: "https://m.media-amazon.com/images/I/61zGOvBSgAL._AC_UF1000,1000_QL80_.jpg",
      rating: 4.6,
      category: "Self Help",
      description: "From the legendary music producer, a master at helping people connect with the wellsprings of their creativity, comes a beautifully crafted book many years in the making that offers that same deep wisdom to all of us.",
      publishedDate: "2023-01-10"
    }
  ];

  const categories = ['All', ...new Set(books.map(book => book.category))];

  const filteredBooks = selectedCategory === 'All' 
    ? books 
    : books.filter(book => book.category === selectedCategory);

  const handleQuickView = (bookId) => {
    navigate(`/quick-view/${bookId}`);
  };

  const handleBorrow = (book) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    borrowBook(book);
  };

  return (
    <div className="books-container">
      <div className="categories">
        {categories.map(category => (
          <button
            key={category}
            className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>
      <div className="books-grid">
        {filteredBooks.map(book => (
          <div key={book.id} className="book-card">
            <div className="book-cover">
              <img src={book.cover} alt={book.title} />
              <div className="book-actions">
                <button onClick={() => handleQuickView(book.id)}>Quick View</button>
                <button onClick={() => handleBorrow(book)}>Borrow</button>
              </div>
            </div>
            <div className="book-info">
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <div className="rating">{'⭐'.repeat(Math.floor(book.rating))} {book.rating}</div>
              <p className="published-date">Published: {book.publishedDate}</p>
              <span className="category-tag">{book.category}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Books;
