import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useWishlist } from '../../../context/WishlistContext';
import { useBorrow } from '../../../context/BorrowContext';
import { useAuth } from '../../../context/AuthContext';
import './Books.css';

const Books = () => {
  const navigate = useNavigate();
  const { addToWishlist, removeFromWishlist, isInWishlist } = useWishlist();
  const { borrowBook, isBorrowed } = useBorrow();
  const { isAuthenticated } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');

  const handleWishlist = (book) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    if (isInWishlist(book.id)) {
      removeFromWishlist(book.id);
    } else {
      addToWishlist(book);
    }
  };

  const handleBorrow = (book) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    borrowBook(book);
    navigate('/borrow-history');
  };

  const handleQuickView = (bookId) => {
    navigate(`/quick-view/${bookId}`);
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const books = [
    {
      id: 1,
      title: "Iron Flame",
      author: "Rebecca Yarros",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781649374172.jpg",
      rating: 4.5,
      genre: "Fantasy",
      description: "The second book in the Empyrean series that follows Violet Sorrengail as she enters her second year at Basgiath War College.",
      available: true
    },
    {
      id: 2,
      title: "House of Flame and Shadow",
      author: "Sarah J. Maas",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781635574104.jpg",
      rating: 4.8,
      genre: "Fantasy",
      description: "The third installment in the Crescent City series follows Bryce Quinlan as she navigates a new world of danger and intrigue.",
      available: true
    },
    {
      id: 3,
      title: "Fourth Wing",
      author: "Rebecca Yarros",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781649374042.jpg",
      rating: 4.7,
      genre: "Fantasy",
      description: "Twenty-year-old Violet Sorrengail was supposed to enter the Scribe Quadrant, but now must become a dragon rider at the elite Basgiath War College.",
      available: true
    },
    {
      id: 4,
      title: "பொன்னியின் செல்வன்",
      author: "கல்கி கிருஷ்ணமூர்த்தி",
      cover: "https://rukminim2.flixcart.com/image/850/1000/xif0q/book/m/h/s/ponniyin-selvan-part-1-new-floods-original-imagg5gzghy4uhv7.jpeg",
      rating: 4.9,
      genre: "Historical Fiction",
      description: "A masterpiece of Tamil historical fiction that follows the story of Arulmozhivarman, who later became Raja Raja Chola I. Set in the 10th century, it weaves together political intrigue, romance, and adventure in the Chola empire.",
      available: true
    },
    {
      id: 5,
      title: "The Creative Act",
      author: "Rick Rubin",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780593652886.jpg",
      rating: 4.6,
      genre: "Self-Help",
      description: "The legendary music producer shares his wisdom on creativity and artistry, offering insights into the creative process.",
      available: true
    },
    {
      id: 6,
      title: "Tomorrow, and Tomorrow, and Tomorrow",
      author: "Gabrielle Zevin",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780593321201.jpg",
      rating: 4.4,
      genre: "Literary Fiction",
      description: "A story of love, friendship, and video game design spanning three decades and multiple continents.",
      available: true
    },
    {
      id: 7,
      title: "Demon Copperhead",
      author: "Barbara Kingsolver",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780063251922.jpg",
      rating: 4.7,
      genre: "Literary Fiction",
      description: "A modern retelling of Charles Dickens's David Copperfield set in Appalachia, following a boy born to a single mother in a single-wide trailer.",
      available: true
    },
    {
      id: 8,
      title: "Lessons in Chemistry",
      author: "Bonnie Garmus",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780385547345.jpg",
      rating: 4.5,
      genre: "Historical Fiction",
      description: "A chemist becomes a cooking show host in the 1960s and uses her platform to educate housewives about science.",
      available: true
    },
    {
      id: 9,
      title: "The Light We Carry",
      author: "Michelle Obama",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780593237465.jpg",
      rating: 4.8,
      genre: "Self-Help",
      description: "The former first lady shares practical wisdom and strategies for staying hopeful in uncertain times.",
      available: true
    },
    {
      id: 10,
      title: "Atomic Habits",
      author: "James Clear",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780735211292.jpg",
      rating: 4.9,
      genre: "Self-Help",
      description: "A proven framework for improving every day through small changes in behavior and mindset.",
      available: true
    }
  ];

  const filteredBooks = books.filter(book => 
    book.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    book.author.toLowerCase().includes(searchQuery.toLowerCase()) ||
    book.genre.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="books-container">
      <div className="books-header">
        <h1>Library Books</h1>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search by title, author, or genre..."
            value={searchQuery}
            onChange={handleSearch}
          />
        </div>
      </div>
      <div className="books-grid">
        {filteredBooks.map(book => (
          <div key={book.id} className="book-card">
            <div className="book-cover">
              <img src={book.cover} alt={book.title} loading="lazy" />
              <div className="book-overlay">
                <button 
                  className="quick-view-btn"
                  onClick={() => handleQuickView(book.id)}
                >
                  Quick View
                </button>
                {book.available && !isBorrowed(book.id) && (
                  <button 
                    className="borrow-btn"
                    onClick={() => handleBorrow(book)}
                  >
                    Borrow Now
                  </button>
                )}
                <button 
                  className={`wishlist-btn ${isInWishlist(book.id) ? 'in-wishlist' : ''}`}
                  onClick={() => handleWishlist(book)}
                >
                  {isInWishlist(book.id) ? 'In Wishlist' : 'Add to Wishlist'}
                </button>
              </div>
            </div>
            <div className="book-info">
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <div className="book-details">
                <span className="rating">{'⭐'.repeat(Math.floor(book.rating))} {book.rating}</span>
                <span className="genre">{book.genre}</span>
              </div>
              <p className="description">{book.description}</p>
              <div className="availability-badge" data-available={book.available && !isBorrowed(book.id)}>
                {book.available && !isBorrowed(book.id) ? 'Available' : 'Not Available'}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Books;
