import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const features = [
    {
      title: 'Browse Books',
      description: 'Explore our extensive collection of books and check their availability status.',
      icon: 'ðŸ“š',
      link: '/books'
    },
    {
      title: 'Borrow History',
      description: 'Track borrowed books and view your borrowing history.',
      icon: 'ðŸ“‹',
      link: '/history'
    },
    {
      title: 'Add New Book',
      description: 'Add new books to expand our library collection.',
      icon: 'âž•',
      link: '/add-book'
    }
  ];

  return (
    <div className="home">
      <div className="hero-section">
        <h1>Welcome to Library Management System</h1>
        <p>Your digital gateway to knowledge and literature</p>
      </div>

      <div className="features-grid">
        {features.map((feature, index) => (
          <div key={index} className="feature-card">
            <div className="feature-icon">{feature.icon}</div>
            <h2>{feature.title}</h2>
            <p>{feature.description}</p>
            <Link to={feature.link} className="feature-link">
              Get Started
              <span className="arrow">â†’</span>
            </Link>
          </div>
        ))}
      </div>

      <div className="quick-stats">
        <div className="stat-card">
          <h3>Total Books</h3>
          <p className="stat-number">1,234</p>
        </div>
        <div className="stat-card">
          <h3>Active Borrowers</h3>
          <p className="stat-number">89</p>
        </div>
        <div className="stat-card">
          <h3>Books Available</h3>
          <p className="stat-number">1,100</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
