import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../../context/AuthContext';
import { useBorrow } from '../../../context/BorrowContext';
import { useWishlist } from '../../../context/WishlistContext';
import './Books.css';

const Books = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const { isAuthenticated } = useAuth();
  const { borrowBook } = useBorrow();
  const { addToWishlist, removeFromWishlist, isInWishlist } = useWishlist();
  const navigate = useNavigate();

  const books = [
    {
      id: 1,
      title: "The Psychology of Money",
      author: "Morgan Housel",
      cover: "https://m.media-amazon.com/images/I/71TRUbzcvaL._AC_UF1000,1000_QL80_.jpg",
      rating: 4.6,
      genre: "Finance",
      description: "Timeless lessons on wealth, greed, and happiness doing well with money isn't necessarily about what you know. It's about how you behave. And behavior is hard to teach, even to really smart people.",
      available: true
    },
    {
      id: 2,
      title: "Atomic Habits",
      author: "James Clear",
      cover: "https://m.media-amazon.com/images/I/81bGKUa1e0L._AC_UF1000,1000_QL80_.jpg",
      rating: 4.8,
      genre: "Self-Help",
      description: "No matter your goals, Atomic Habits offers a proven framework for improving--every day. James Clear, one of the world's leading experts on habit formation, reveals practical strategies that will teach you exactly how to form good habits, break bad ones, and master the tiny behaviors that lead to remarkable results.",
      available: true
    },
    {
      id: 3,
      title: "Deep Work",
      author: "Cal Newport",
      cover: "https://m.media-amazon.com/images/I/71QKQ9mwV7L._AC_UF1000,1000_QL80_.jpg",
      rating: 4.5,
      genre: "Productivity",
      description: "Deep work is the ability to focus without distraction on a cognitively demanding task. It's a skill that allows you to quickly master complicated information and produce better results in less time.",
      available: true
    },
    {
      id: 4,
      title: "பொன்னியின் செல்வன்",
      author: "கல்கி கிருஷ்ணமூர்த்தி",
      cover: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSHd7H5dXOZgxAHBGzQcYQXiJX2JYdvBPJ1Ow&usqp=CAU",
      rating: 4.9,
      genre: "Historical Fiction",
      description: "A masterpiece of Tamil historical fiction that follows the story of Arulmozhivarman, who later became Raja Raja Chola I. Set in the 10th century, it weaves together political intrigue, romance, and adventure in the Chola empire.",
      available: true
    },
    {
      id: 5,
      title: "The Creative Act",
      author: "Rick Rubin",
      cover: "https://m.media-amazon.com/images/I/61zGOvBSgAL._AC_UF1000,1000_QL80_.jpg",
      rating: 4.6,
      genre: "Self Help",
      description: "From the legendary music producer, a master at helping people connect with the wellsprings of their creativity, comes a beautifully crafted book many years in the making that offers that same deep wisdom to all of us.",
      available: true
    },
    {
      id: 6,
      title: "Tomorrow, and Tomorrow, and Tomorrow",
      author: "Gabrielle Zevin",
      cover: "https://m.media-amazon.com/images/I/81zxQz8nY+L._AC_UF1000,1000_QL80_.jpg",
      rating: 4.5,
      genre: "Fiction",
      description: "In this exhilarating novel, two friends–often in love, but never lovers–come together as creative partners in the world of video game design, where success brings them fame, joy, tragedy, duplicity, and, ultimately, a kind of immortality.",
      available: true
    }
  ];

  const filteredBooks = books.filter(book => {
    const searchLower = searchQuery.toLowerCase();
    return (
      book.title.toLowerCase().includes(searchLower) ||
      book.author.toLowerCase().includes(searchLower) ||
      book.genre.toLowerCase().includes(searchLower)
    );
  });

  const handleBorrow = (book) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    borrowBook(book);
    navigate('/borrow-history');
  };

  const handleWishlist = (book) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    if (isInWishlist(book.id)) {
      removeFromWishlist(book.id);
    } else {
      addToWishlist(book);
    }
  };

  return (
    <div className="books-container">
      <div className="books-header">
        <h1>Library Collection</h1>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search by title, author, or genre..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
      </div>

      <div className="books-grid">
        {filteredBooks.map((book) => (
          <div key={book.id} className="book-card">
            <div className="book-cover">
              <img src={book.cover} alt={book.title} />
              <div className="book-overlay">
                <button
                  className="borrow-btn"
                  onClick={() => handleBorrow(book)}
                >
                  Borrow Now
                </button>
                <button
                  className={`wishlist-btn ${isInWishlist(book.id) ? 'in-wishlist' : ''}`}
                  onClick={() => handleWishlist(book)}
                >
                  {isInWishlist(book.id) ? 'Remove from Wishlist' : 'Add to Wishlist'}
                </button>
              </div>
            </div>
            <div className="book-info">
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <div className="book-details">
                <span className="rating">★ {book.rating}</span>
                <span className="genre">{book.genre}</span>
              </div>
              <p className="description">{book.description}</p>
              <span 
                className="availability-badge"
                data-available={book.available}
              >
                {book.available ? 'Available' : 'Not Available'}
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Books;
