import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const navigate = useNavigate();

  const handleQuickView = (bookId) => {
    navigate(`/quick-view/${bookId}`);
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle('dark-mode');
  };

  const newArrivals = [
    {
      id: 1,
      title: "Iron Flame",
      author: "Rebecca Yarros",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781649374172.jpg",
      date: "2025-01-25",
      genre: "Fantasy"
    },
    {
      id: 2,
      title: "The Heaven & Earth Grocery Store",
      author: "James McBride",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780593422946.jpg",
      date: "2025-01-22",
      genre: "Historical Fiction"
    },
    {
      id: 3,
      title: "House of Flame and Shadow",
      author: "Sarah J. Maas",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781635574104.jpg",
      date: "2025-01-15",
      genre: "Fantasy"
    },
    {
      id: 4,
      title: "Fourth Wing",
      author: "Rebecca Yarros",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781649374042.jpg",
      date: "2025-01-10",
      genre: "Fantasy"
    },
    {
      id: 5,
      title: "The Creative Act",
      author: "Rick Rubin",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780593652886.jpg",
      date: "2025-01-05",
      genre: "Self-Help"
    },
    {
      id: 6,
      title: "Tomorrow, and Tomorrow, and Tomorrow",
      author: "Gabrielle Zevin",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780593321201.jpg",
      date: "2025-01-01",
      genre: "Literary Fiction"
    },
    {
      id: 7,
      title: "Hello Beautiful",
      author: "Ann Napolitano",
      cover: "https://prodimage.images-bn.com/pimages/9780593243732_p0_v2_s1200x630.jpg",
      date: "2025-01-18",
      genre: "Contemporary Fiction"
    },
    {
      id: 8,
      title: "Demon Copperhead",
      author: "Barbara Kingsolver",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780063251922.jpg",
      date: "2025-01-12",
      genre: "Literary Fiction"
    },
    {
      id: 9,
      title: "Yellowface",
      author: "R.F. Kuang",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780063250833.jpg",
      date: "2025-01-08",
      genre: "Thriller"
    },
    {
      id: 10,
      title: "Happy Place",
      author: "Emily Henry",
      cover: "https://storage.googleapis.com/du-prd/books/images/9780593441275.jpg",
      date: "2025-01-03",
      genre: "Romance"
    }
  ];

  return (
    <div className={`home-container ${isDarkMode ? 'dark-mode' : ''}`}>
      <div className="theme-toggle">
        <button onClick={toggleDarkMode}>
          {isDarkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
        </button>
      </div>

      {/* Search Bar */}
      <div className="search-section">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search for books, authors, or genres..."
            className="search-input"
          />
          <button className="search-button">Search</button>
        </div>
      </div>

      {/* New Arrivals Section with Enhanced Design */}
      <section className="new-arrivals-section">
        <h2>New Arrivals</h2>
        <p className="section-subtitle">Discover our latest additions to the library collection</p>
        <div className="new-arrivals-grid">
          {newArrivals.map(book => (
            <div key={book.id} className="new-arrival-card">
              <div className="book-cover">
                <img 
                  src={book.cover} 
                  alt={book.title}
                  loading="lazy"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = 'https://placehold.co/400x600/1e40af/ffffff?text=' + encodeURIComponent(book.title);
                  }}
                />
                <div className="book-overlay">
                  <span className="new-badge">New</span>
                  <span className="genre-badge">{book.genre}</span>
                  <button className="quick-view-btn" onClick={() => handleQuickView(book.id)}>Quick View</button>
                </div>
              </div>
              <div className="book-info">
                <h3>{book.title}</h3>
                <p className="author">by {book.author}</p>
                <p className="arrival-date">Added {new Date(book.date).toLocaleDateString('en-US', {
                  month: 'short',
                  day: 'numeric',
                  year: 'numeric'
                })}</p>
                <button className="borrow-now-btn">Borrow Now</button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
