import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Wishlist.css';

const Wishlist = () => {
  const navigate = useNavigate();

  const wishlistBooks = [
    {
      id: 1,
      title: "Iron Flame",
      author: "Rebecca Yarros",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781649374172.jpg",
      addedDate: "2025-01-25",
      available: true
    },
    {
      id: 2,
      title: "House of Flame and Shadow",
      author: "Sarah J. Maas",
      cover: "https://storage.googleapis.com/du-prd/books/images/9781635574104.jpg",
      addedDate: "2025-01-15",
      available: false
    }
  ];

  const handleQuickView = (bookId) => {
    navigate(`/quick-view/${bookId}`);
  };

  return (
    <div className="wishlist-container">
      <div className="wishlist-header">
        <h1>My Wishlist</h1>
        <p>Books you want to read in the future</p>
      </div>

      <div className="wishlist-grid">
        {wishlistBooks.map(book => (
          <div key={book.id} className="wishlist-card">
            <div className="book-cover">
              <img src={book.cover} alt={book.title} />
              <div className="book-overlay">
                <button 
                  className="quick-view-btn"
                  onClick={() => handleQuickView(book.id)}
                >
                  Quick View
                </button>
                {book.available && (
                  <button className="borrow-btn">
                    Borrow Now
                  </button>
                )}
                <button className="remove-btn">
                  Remove from Wishlist
                </button>
              </div>
            </div>
            <div className="book-info">
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <p className="added-date">
                Added on {new Date(book.addedDate).toLocaleDateString()}
              </p>
              <div className="availability-badge" data-available={book.available}>
                {book.available ? 'Available' : 'Not Available'}
              </div>
            </div>
          </div>
        ))}
      </div>

      {wishlistBooks.length === 0 && (
        <div className="empty-wishlist">
          <h2>Your wishlist is empty</h2>
          <p>Browse our library and add books you'd like to read!</p>
          <button 
            className="browse-books-btn"
            onClick={() => navigate('/books')}
          >
            Browse Books
          </button>
        </div>
      )}
    </div>
  );
};

export default Wishlist;
