import React, { Component } from 'react';
import './BorrowHistory.css';

class BorrowHistory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      borrowHistory: [
        {
          id: 1,
          bookTitle: 'The Great Gatsby',
          borrower: 'John Doe',
          borrowDate: '2025-01-15',
          returnDate: '2025-01-29',
          status: 'Returned',
          bookCover: 'https://example.com/gatsby.jpg'
        },
        {
          id: 2,
          bookTitle: 'To Kill a Mockingbird',
          borrower: 'Jane Smith',
          borrowDate: '2025-01-20',
          returnDate: null,
          status: 'Borrowed',
          bookCover: 'https://example.com/mockingbird.jpg'
        }
      ],
      filterStatus: 'all'
    };
  }

  handleStatusFilter = (e) => {
    this.setState({ filterStatus: e.target.value });
  };

  getFilteredHistory = () => {
    const { borrowHistory, filterStatus } = this.state;
    if (filterStatus === 'all') return borrowHistory;
    return borrowHistory.filter(record => 
      record.status.toLowerCase() === filterStatus.toLowerCase()
    );
  };

  formatDate = (dateString) => {
    if (!dateString) return 'Not returned';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  render() {
    const filteredHistory = this.getFilteredHistory();

    return (
      <div className="borrow-history">
        <div className="history-header">
          <h2>Borrow History</h2>
          <div className="status-filter">
            <select
              value={this.state.filterStatus}
              onChange={this.handleStatusFilter}
            >
              <option value="all">All Status</option>
              <option value="borrowed">Borrowed</option>
              <option value="returned">Returned</option>
            </select>
          </div>
        </div>

        <div className="history-grid">
          {filteredHistory.map(record => (
            <div key={record.id} className="history-card">
              <div className="book-thumbnail">
                <img src={record.bookCover} alt={record.bookTitle} />
                <div className={`status-badge ${record.status.toLowerCase()}`}>
                  {record.status}
                </div>
              </div>
              <div className="history-details">
                <h3>{record.bookTitle}</h3>
                <div className="detail-row">
                  <span className="label">Borrower:</span>
                  <span className="value">{record.borrower}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Borrowed:</span>
                  <span className="value">{this.formatDate(record.borrowDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Returned:</span>
                  <span className="value">{this.formatDate(record.returnDate)}</span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {filteredHistory.length === 0 && (
          <div className="no-history">
            <p>No borrow history found for the selected filter.</p>
          </div>
        )}
      </div>
    );
  }
}

export default BorrowHistory;
